# coding=utf-8# @author   ：迷心兔# time      ：2019/11/10from django import formsfrom django.test import TestCasefrom ..forms import PostForm  # 文章表单from ..templatetags.form_tags import field_type, input_class# 测试新建页面的组件class FieldTypeTests(TestCase):    # 测试字段小部件类型    def test_field_widget_type(self):        form = PostForm( )        self.assertEquals('TextInput', field_type(form['title']))        self.assertEquals('Textarea', field_type(form['text']))# 用户名或密码输入测试class InputClassTests(TestCase):    # 没有输入数据的测试    def test_unbound_field_initial_state(self):        form = PostForm( )        self.assertEquals('form-control ', input_class(form['title']))    # 输入错误数据的测试    def test_valid_bound_field(self):        form = PostForm({'title': 'test_title', 'text': 'test_text'})        self.assertEquals('form-control is-valid', input_class(form['title']))    # 输入数据不完整的测试    def test_invalid_bound_field(self):        form = PostForm({'title': '', 'text': 'test_text'})        self.assertEquals('form-control is-invalid', input_class(form['title']))